create table "SSMU_USERS" ("ID" SERIAL NOT NULL PRIMARY KEY,"EMAIL" VARCHAR(254) NOT NULL,"USERNAME" VARCHAR(254) NOT NULL,"FIRSTNAME" VARCHAR(254),"LASTNAME" VARCHAR(254),"PASSWORD" VARCHAR(254) NOT NULL,"POWER" VARCHAR(254) NOT NULL);
create table "SSMU_PROFILES" ("ID" SERIAL NOT NULL PRIMARY KEY,"USER_ID" INTEGER NOT NULL,"NAME" VARCHAR(254) NOT NULL,"DESCRIPTION" VARCHAR(254),"IMAGE_URL" VARCHAR(254),"WEIGHT_CLASS" VARCHAR(254),"FACEBOOK" VARCHAR(254),"YOUTUBE" VARCHAR(254),"INSTAGRAM" VARCHAR(254));
create table "SSMU_RECORDS" ("ID" SERIAL NOT NULL PRIMARY KEY,"USER_ID" INTEGER NOT NULL,"NAME" VARCHAR(254) NOT NULL,"WEIGHT_CLASS" VARCHAR(254) NOT NULL,"SEX" VARCHAR(254) NOT NULL,"SQUAT" DOUBLE PRECISION NOT NULL,"BENCH" DOUBLE PRECISION NOT NULL,"DEADLIFT" DOUBLE PRECISION NOT NULL,"TOTAL" DOUBLE PRECISION NOT NULL,"WILKS" DOUBLE PRECISION NOT NULL,"DATE" DATE NOT NULL,"COMPETITION" BOOLEAN NOT NULL);
create table "NEWS_ITEMS" ("ID" SERIAL NOT NULL PRIMARY KEY,"TITLE" VARCHAR(254) NOT NULL,"CONTENT" TEXT NOT NULL,"AUTHOR" VARCHAR(254) NOT NULL,"DATE" DATE NOT NULL,"USER_ID" INTEGER NOT NULL);
alter table "SSMU_PROFILES" add constraint "USER_PROFILE_FK" foreign key("USER_ID") references "SSMU_USERS"("ID") on update NO ACTION on delete NO ACTION;
alter table "SSMU_RECORDS" add constraint "USER_RECORD_FK" foreign key("USER_ID") references "SSMU_USERS"("ID") on update NO ACTION on delete NO ACTION;
alter table "NEWS_ITEMS" add constraint "USER_NEWS_FK" foreign key("USER_ID") references "SSMU_USERS"("ID") on update NO ACTION on delete NO ACTION